import 'bootstrap/dist/css/bootstrap.min.css'
import React, { useState, useEffect } from 'react'
import Container from 'react-bootstrap/Container'
import Jumbotron from 'react-bootstrap/Jumbotron'
import Row from 'react-bootstrap/Row'
import Col from 'react-bootstrap/Col'
import Button from 'react-bootstrap/Button'
// Import components
import List from './components/List'
import Form from './components/Form'
import { getNotes } from './utils/noteHelpers'

export default function App({ theme }) {
	const [selectedNote, setSelectedNote] = useState(undefined)
	const [notes, setNotes] = useState([])

	useEffect(() => {
		const notes = getNotes()
		setNotes(notes)
	}, [])

	const refreshList = () => {
		setSelectedNote(undefined)
		const notes = getNotes()
		setNotes([...notes])
	}

	const onClickNewNote = () => setSelectedNote(undefined)

	return (
		<Container>
			<Jumbotron fluid theme="light">
				<h1 theme="light">Todo App</h1>
			</Jumbotron>
			<Row>
				<Col xs={12} md={4} lg={6}>
					<Button onClick={onClickNewNote} variant="dark" block>
						New note
					</Button>
					<List
						notes={notes}
						selectedNote={selectedNote}
						setSelectedNote={setSelectedNote}
					/>
				</Col>
				<Col xs={12} md={8} lg={6}>
					{/* <Editor /> */}
					<Form refreshList={refreshList} selectedNote={selectedNote} />
				</Col>
			</Row>
		</Container>
	)
}

const themes = {
	light: {
		backgroundColor: 'white',
		textColor: 'red'
	},
	dark: {
		backgroundColor: 'red',
		textColor: 'white'
	}
}
