/*
C: Create a note
R: Read (get) a note
U: Update a note
D: Delete a note
*/

// const notes = [
// 	{ id: 1, title: 'New note', body: 'Hey!' },
// 	{ id: 2, title: 'Second note', body: 'Empty' },
// 	{ id: 3, title: 'Third note', body: 'Something' }
// ]

function initializeNotes() {
	// sets localstorage for the first time
	localStorage.setItem('notes', JSON.stringify([]))
	// returns an array
	return []
}

export function createNote(title, body) {
	const notes = getNotes()
	const note = {
		id: Date.now(),
		title,
		body
	}
	notes.unshift(note)
	return note
}

export function getNote(id) {
	const notes = getNotes()
	return notes.find((note) => note.id === id)
}

export function getNotes() {
	const notes = localStorage.getItem('notes')
	if (!notes) {
		notes = initializeNotes()
	}
	const parsedNotes = JSON.parse(notes)
	return parsedNotes
}

export function updateNote(id, title, body) {
	const indexToUpdate = notes.findIndex((note) => note.id === id)
	const note = {
		id,
		title,
		body
	}
	// removes old note
	notes.splice(indexToUpdate, 1)
	// adds new note to start
	notes.splice(0, 0, note)
	return notes
}

// updateNote(3, 'hello', 'blabla')

export function deleteNote(id) {
	const indexToDelete = notes.findIndex((note) => note.id === id)
	if (indexToDelete >= 0) notes.splice(indexToDelete, 1)
	return true
}
