import React, { useState, useEffect, useContext } from 'react'

// Import Bootstrap
import 'bootstrap/dist/css/bootstrap.min.css'
import Container from 'react-bootstrap/Container'
import Jumbotron from 'react-bootstrap/Jumbotron'
import Row from 'react-bootstrap/Row'
import Col from 'react-bootstrap/Col'
import Button from 'react-bootstrap/Button'
import { LanguageContext } from '../context/LanguageContext'
import { ThemeContext } from '../context/ThemeContext'

// Import Components
import List from './List'
import Editor from './Editor'

// Import Other
import { getNotes } from '../utils/noteHelpers'
import Picture from '../Picture'

export default function Notes() {
	const [selectedNote, setSelectedNote] = useState(undefined)
	const [showArchive, setShowArchive] = useState(false)
	const [notes, setNotes] = useState([])
	const { lang } = React.useContext(LanguageContext)
	const theme = useContext(ThemeContext)

	useEffect(() => {
		const notes = getNotes()
		setNotes(notes)
	}, [])

	const refreshList = () => {
		setSelectedNote(undefined)
		const notes = getNotes()
		setNotes([...notes])
	}

	const onClickNewNote = () => setSelectedNote(undefined)

	return (
		<Container>
			<Jumbotron fluid>
				<h1>Todo App</h1>
			</Jumbotron>
			<Row>
				<Col xs={12} md={3} lg={4}>
					<Button onClick={onClickNewNote} className="mb-4" variant="dark" block>
						New note
					</Button>
					<List
						notes={notes}
						selectedNote={selectedNote}
						setSelectedNote={setSelectedNote}
					/>
					<Button onClick={onClickNewNote} className="mt-4" variant="secondary" block>
						<i class="fas fa-inbox" /> Archive
					</Button>
				</Col>
				<Col xs={6} md={7} lg={6}>
					<Editor
						style={{ ...theme }}
						refreshList={refreshList}
						selectedNote={selectedNote}
					/>
				</Col>
				<Col xs={6} md={2} lg={2}>
					<Picture src="https://source.unsplash.com/random/100x100">
						<p>{lang['caption']}</p>
					</Picture>
				</Col>
			</Row>
		</Container>
	)
}
