import React, { useState, useEffect } from 'react'
import Form from 'react-bootstrap/Form'
import Button from 'react-bootstrap/Button'
import { createNote, getNotes, updateNote } from '../utils/noteHelpers'

/*
Required
1. (DONE) write text in fields and store it as a new note
2. (DONE) click on a note and display text in the fields
3. (DONE) edit the field w/ text from a selected note and save it
4. clicking save while on a selected note will update the selected note
*/

export default function InputForm({ selectedNote }) {
	const [title, setTitle] = useState('')

	useEffect(
		() => {
			if (selectedNote) setTitle(selectedNote.title)
		},
		[selectedNote]
	)

	const onChangeTitle = (e) => setTitle(e.target.value)
	const onSave = (e) => {
		e.preventDefault()
		if (selectedNote) {
			updateNote(selectedNote.id, title)
			console.log(getNotes())
			return
		}
		createNote(title, '')
		console.log(getNotes())
	}

	return (
		<Form>
			<Form.Group>
				<Form.Label>Title</Form.Label>
				<Form.Control className="mb-4" size="lg" value={title} onChange={onChangeTitle} />
			</Form.Group>
			<Button onClick={onSave} className="mr-2" variant="success">
				Save
			</Button>
		</Form>
	)
}
