import React, { useState } from 'react'
import Form from 'react-bootstrap/Form'

/*
Required
1. write text in fields and store it as a new note
2. click on a note and display text in the fields
3. edit the field w/ text from a selected note and save it
*/

export default function inputForm() {
	const [title, setTitle] = useState(undefined)
	const onInput = (e) => setTitle(e.target.value)

	return (
		<Form>
			<Form.Group>
				<Form.Label>Title</Form.Label>
				<Form.Control
					className="mb-4"
					size="lg"
					value="this wont change"
					onChange={onInput}
				/>
			</Form.Group>
		</Form>
	)
}
