import React, { useState, useEffect } from 'react'

// Import Bootstrap
import 'bootstrap/dist/css/bootstrap.min.css'
import Container from 'react-bootstrap/Container'
import Jumbotron from 'react-bootstrap/Jumbotron'
import Row from 'react-bootstrap/Row'
import Col from 'react-bootstrap/Col'
import Button from 'react-bootstrap/Button'
import LanguageProvider, { LanguageContext } from '../context/LanguageContext'

// Import Components
import List from './List'
import Editor from './Editor'

// Import Other
import { getNotes } from '../utils/noteHelpers'
import Picture from '../Picture'

export default function Notes() {
	const [selectedNote, setSelectedNote] = useState(undefined)
	const [notes, setNotes] = useState([])
	const lang = React.useContext(LanguageContext)

	useEffect(() => {
		const notes = getNotes()
		setNotes(notes)
	}, [])

	const refreshList = () => {
		setSelectedNote(undefined)
		const notes = getNotes()
		setNotes([...notes])
	}

	const onClickNewNote = () => setSelectedNote(undefined)

	return (
		<Container>
			<Jumbotron fluid>
				<h1>Todo App</h1>
			</Jumbotron>
			<Row>
				<Col xs={12} md={4} lg={6}>
					<Button onClick={onClickNewNote} variant="dark" block>
						New note
					</Button>
					<List
						notes={notes}
						selectedNote={selectedNote}
						setSelectedNote={setSelectedNote}
					/>
				</Col>
				<Col xs={12} md={8} lg={6}>
					<Editor theme="dark" refreshList={refreshList} selectedNote={selectedNote} />
				</Col>
			</Row>
			<Picture src="https://source.unsplash.com/random/150x150">
				<p>{lang['caption']}</p>
			</Picture>
		</Container>
	)
}
