import React, { useState, useEffect } from 'react'
import Form from 'react-bootstrap/Form'
import Button from 'react-bootstrap/Button'
import { createNote, getNotes, updateNote, deleteNote } from '../utils/noteHelpers'

/*
Required
1. (DONE) write text in fields and store it as a new note
2. (DONE) click on a note and display text in the fields
3. (DONE) edit the field w/ text from a selected note and save it
4. (DONE) clicking save while on a selected note will update the selected note
	-- how to we make <List /> rerender when clicking Save?
5. Delete selected note
*/

export default function InputForm({ selectedNote, refreshList }) {
	const [title, setTitle] = useState('')

	useEffect(
		() => {
			if (selectedNote) setTitle(selectedNote.title)
		},
		[selectedNote]
	)

	const onChangeTitle = (e) => setTitle(e.target.value)
	const onSave = (e) => {
		e.preventDefault()
		if (selectedNote) {
			updateNote(selectedNote.id, title)
			return refreshList()
		}

		createNote(title, '')
		refreshList()
	}

	const onDelete = (e) => {
		e.preventDefault()
		const { id } = selectedNote
	}

	return (
		<Form>
			<Form.Group>
				<Form.Label>Title</Form.Label>
				<Form.Control className="mb-4" size="lg" value={title} onChange={onChangeTitle} />
			</Form.Group>
			<Button onClick={onSave} className="mr-2" variant="success">
				Save
			</Button>
			{selectedNote && (
				<Button onDelete={onDelete} className="mr-2" variant="danger">
					Delete
				</Button>
			)}
		</Form>
	)
}
