/*
C: Create a note
R: Read (get) a note
U: Update a note
D: Delete a note
*/

export const notes = [
	{ id: 1, title: 'New note', body: 'Hey!' },
	{ id: 2, title: 'Second note', body: 'Empty' },
	{ id: 3, title: 'Third note', body: 'Something' }
]

export function createNote(title, body) {
	const note = {
		id: notes.length + 1,
		title,
		body
	}
	notes.push(note)
	return note
}

export function getNote(id) {
	const note = notes.find((note) => note.id === id)
}
// const note = getNote(3)

export function getNotes(id) {
	return notes
}

export function updateNote(id, title, body) {}

export function deleteNote() {}
