import React, { useState } from 'react'
import Form from 'react-bootstrap/Form'
import Button from 'react-bootstrap/Button'
import { createNote, getNotes } from '../utils/noteHelpers'

/*
Required
1. write text in fields and store it as a new note
2. click on a note and display text in the fields
3. edit the field w/ text from a selected note and save it
*/

export default function InputForm() {
	const [title, setTitle] = useState('')
	console.log('title', title)
	const onChangeTitle = (e) => setTitle(e.target.value)

	// const [body, setBody] = useState(undefined)
	// console.log('body', body)
	// const onChangeBody = (e) => setBody(e.target.value)

	const onSave = (e) => {
		e.preventDefault()
		console.log('Saving new note:', title)
		createNote(title, '')
		console.log(getNotes())
	}

	return (
		<Form>
			<Form.Group>
				<Form.Label>Title</Form.Label>
				<Form.Control className="mb-4" size="lg" onChange={onChangeTitle} />
			</Form.Group>
			{/* <Form.Group>
				<Form.Label>Body Content</Form.Label>
				<Form.Control className="mb-4" size="lg" onChange={onChangeBody} />
			</Form.Group> */}
			<Button onClick={onSave} className="mr-2" variant="success">
				Save
			</Button>
		</Form>
	)
}
